import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Subject } from '../../models/subject.model';
import { Student } from '../../models/student.model';
import { SubjectService } from '../../services/subject.service';
import { AuthService } from '../../services/auth.service';
import { RegistrationService } from '../../services/registration.service';

@Component({
  selector: 'app-subject-detail',
  templateUrl: './subject-detail.component.html',
  styleUrls: ['./subject-detail.component.css']
})
export class SubjectDetailComponent implements OnInit {
  subject: Subject | null = null;
  students: Student[] = [];
  classmates: Student[] = [];
  loading = false;
  error = '';
  currentUserId: number | null = null;
  isStudent = false;

  constructor(
    private route: ActivatedRoute,
    private subjectService: SubjectService,
    private authService: AuthService,
    private registrationService: RegistrationService
  ) { }

  ngOnInit(): void {
    this.currentUserId = this.authService.getCurrentUserId();
    this.isStudent = this.authService.isStudent();

    this.route.params.subscribe(params => {
      const id = params['id'];
      // Validate that id is a valid number
      if (id && !isNaN(Number(id))) {
        const subjectId = Number(id);
        this.loadSubject(subjectId);

        // Solo cargar la lista de estudiantes si no es un estudiante
        if (!this.isStudent) {
          this.loadStudents(subjectId);
        } else if (this.currentUserId) {
          // Si es estudiante, cargar sus compañeros de clase
          this.loadClassmates(subjectId, this.currentUserId);
        }
      } else {
        this.error = 'ID de materia inválido';
      }
    });
  }

  loadSubject(id: number): void {
    if (!id || isNaN(id)) {
      this.error = 'ID de materia invu00e1lido';
      return;
    }

    this.loading = true;
    this.subjectService.getSubject(id).subscribe({
      next: (data) => {
        this.subject = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar datos de la materia';
        this.loading = false;
        console.error(err);
      }
    });
  }

  loadStudents(subjectId: number): void {
    this.loading = true;
    this.subjectService.getStudentsBySubject(subjectId).subscribe({
      next: (data) => {
        this.students = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar estudiantes de la materia';
        this.loading = false;
        console.error(err);
      }
    });
  }

  loadClassmates(subjectId: number, studentId: number): void {
    this.loading = true;
    this.registrationService.getClassmates(subjectId, studentId).subscribe({
      next: (data) => {
        this.classmates = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar compañeros de clase';
        this.loading = false;
        console.error(err);
      }
    });
  }
}
