import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { HttpClientModule, HTTP_INTERCEPTORS } from '@angular/common/http';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { JwtModule } from '@auth0/angular-jwt';

import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
//import { StudentListComponent } from './components/student-list/student-list.component';
//import { StudentFormComponent } from './components/student-form/student-form.component';
////import { StudentDetailComponent } from './components/student-detail/student-detail.component';
//import { SubjectListComponent } from './components/subject-list/subject-list.component';
//import { SubjectDetailComponent } from './components/subject-detail/subject-detail.component';
import { ClassmatesComponent } from './components/classmates/classmates.component';
import { StudentCreditsComponent } from './components/student-credits/student-credits.component';
import { SubjectRegistrationComponent } from './components/subject-registration/subject-registration.component';
import { SubjectFormComponent } from './components/subject-form/subject-form.component';
import { ListaMateriasComponente } from './componentes/asignaturas/lista-materias.componente';
// import { RegisterComponent } from './components/auth/register.component';
import { RegistroComponente } from './componentes/autenticacion/registro.componente';
import { ProfileComponent } from './components/auth/profile.component';
import { InicioSesionComponente } from './componentes/autenticacion/inicio-sesion.componente';
import { ProfessorListComponent } from './components/professor-list/professor-list.component';
import { ProfessorFormComponent } from './components/professor-form/professor-form.component';
import { AuthInterceptorService } from './services/auth-interceptor.service';
import { ListaEstudiantesComponente } from './componentes/estudiantes/lista-estudiantes.componente';
import { FormularioEstudianteComponente } from './componentes/estudiantes/formulario-estudiante.componente';
import { DetalleEstudianteComponente } from './componentes/estudiantes/detalle-estudiante.componente';
import { FormularioMateriaComponente } from './componentes/asignaturas/formulario-materia/formulario-materia.componente';
import { DetalleMateriaComponente } from './componentes/asignaturas/detalle-materia.componente';

export function tokenGetter() {
  const userData = localStorage.getItem('userData');
  if (!userData) {
    return null;
  }
  const parsedData = JSON.parse(userData);
  return parsedData._token;
}

@NgModule({
  declarations: [
    AppComponent,
    ListaEstudiantesComponente,
    FormularioEstudianteComponente,
    DetalleEstudianteComponente,
    ListaMateriasComponente,
  //  SubjectDetailComponent,
   // ClassmatesComponent,
  //  StudentCreditsComponent,
 //   SubjectRegistrationComponent,
//    LoginComponent,
//     RegisterComponent,
   // RegistroComponente,
  //  ProfileComponent,
  //  ProfessorListComponent,
 //   ProfessorFormComponent,
    InicioSesionComponente,
 //   SubjectFormComponent,
    FormularioMateriaComponente,
    DetalleMateriaComponente
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    HttpClientModule,
    FormsModule,
    ReactiveFormsModule,
    JwtModule.forRoot({
      config: {
        tokenGetter: tokenGetter,
        allowedDomains: ['localhost:5000'],
        disallowedRoutes: ['localhost:5000/api/auth/login', 'localhost:5000/api/auth/register']
      }
    })
  ],
  providers: [
    { provide: HTTP_INTERCEPTORS, useClass: AuthInterceptorService, multi: true }
  ],
  bootstrap: [AppComponent]
})
export class AppModule { }
