import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Professor } from '../../models/professor.model';
import { ProfessorService } from '../../services/professor.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-professor-list',
  templateUrl: './professor-list.component.html',
  styleUrls: ['./professor-list.component.css']
})
export class ProfessorListComponent implements OnInit {
  professors: Professor[] = [];
  isLoading = false;
  error: string | null = null;
  isAdmin = false;

  constructor(
    private professorService: ProfessorService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.isAdmin = this.authService.isAdmin();
    this.loadProfessors();
  }

  loadProfessors(): void {
    this.isLoading = true;
    this.error = null;

    this.professorService.getProfessors().subscribe({
      next: (data) => {
        this.professors = data;
        this.isLoading = false;
      },
      error: (error) => {
        this.error = error.message || 'Error al cargar los profesores';
        this.isLoading = false;
      }
    });
  }

  onAddProfessor(): void {
    this.router.navigate(['/professors/new']);
  }

  onEditProfessor(id: number): void {
    this.router.navigate(['/professors/edit', id]);
  }

  onDeleteProfessor(id: number): void {
    if (confirm('¿Está seguro de que desea eliminar este profesor?')) {
      this.isLoading = true;
      this.error = null;

      this.professorService.deleteProfessor(id).subscribe({
        next: () => {
          this.loadProfessors();
        },
        error: (error) => {
          this.error = error.message || 'Error al eliminar el profesor';
          this.isLoading = false;
        }
      });
    }
  }
}
