import { Component, Input, OnInit } from '@angular/core';
import { RegistrationService } from '../../services/registration.service';

@Component({
  selector: 'app-student-credits',
  templateUrl: './student-credits.component.html',
  styleUrls: ['./student-credits.component.css']
})
export class StudentCreditsComponent implements OnInit {
  @Input() studentId!: number;

  totalCredits = 0;
  loading = false;
  error = '';
  maxCredits = 9; // 3 materias x 3 créditos cada una

  constructor(private registrationService: RegistrationService) { }

  ngOnInit(): void {
    this.loadCredits();
  }

  loadCredits(): void {
    this.loading = true;
    this.registrationService.getStudentTotalCredits(this.studentId).subscribe({
      next: (credits) => {
        this.totalCredits = credits;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar créditos';
        this.loading = false;
        console.error(err);
      }
    });
  }

  getProgressPercentage(): number {
    return (this.totalCredits / this.maxCredits) * 100;
  }
}
