import { Component, OnInit } from '@angular/core';
import { Subject } from '../../models/subject.model';
import { SubjectService } from '../../services/subject.service';
import { AuthService } from '../../services/auth.service';
import { RegistrationService } from '../../services/registration.service';
import { forkJoin, of } from 'rxjs';
import { switchMap, map } from 'rxjs/operators';

@Component({
  selector: 'app-subject-list',
  templateUrl: './subject-list.component.html',
  styleUrls: ['./subject-list.component.css']
})
export class SubjectListComponent implements OnInit {
  subjects: Subject[] = [];
  loading = false;
  error = '';
  isStudent = false;

  constructor(
    private subjectService: SubjectService,
    private authService: AuthService,
    private registrationService: RegistrationService
  ) { }

  ngOnInit(): void {
    this.isStudent = this.authService.isStudent();
    this.loadSubjects();
  }

  loadSubjects(): void {
    this.loading = true;

    // Si es estudiante, solo mostrar materias registradas
    if (this.isStudent) {
      const currentUserId = this.authService.getCurrentUserId();

      if (currentUserId) {
        this.registrationService.getStudentRegistrations(currentUserId).pipe(
          switchMap(registrations => {
            if (registrations.length === 0) {
              return of([]);
            }

            const subjectIds = registrations.map(reg => reg.subjectId);
            return this.subjectService.getSubjectsByIds(subjectIds);
          })
        ).subscribe({
          next: (data) => {
            this.subjects = data;
            this.loading = false;
          },
          error: (err) => {
            this.error = 'Error al cargar materias registradas';
            this.loading = false;
            console.error(err);
          }
        });
      } else {
        this.loading = false;
        this.error = 'No se pudo identificar al estudiante';
      }
    } else {
      // Si no es estudiante, mostrar todas las materias
      this.subjectService.getSubjects().subscribe({
        next: (data) => {
          this.subjects = data;
          this.loading = false;
        },
        error: (err) => {
          this.error = 'Error al cargar materias';
          this.loading = false;
          console.error(err);
        }
      });
    }
  }
}
