import { Component, Input, OnInit } from '@angular/core';
import { RegistrationService } from '../../services/registration.service';
import { Student } from '../../models/student.model';

@Component({
  selector: 'app-classmates',
  templateUrl: './classmates.component.html',
  styleUrls: ['./classmates.component.css']
})
export class ClassmatesComponent implements OnInit {
  @Input() subjectId!: number;
  @Input() currentStudentId!: number;

  classmateNames: Student[] = [];
  loading = false;
  error = '';

  constructor(private registrationService: RegistrationService) { }

  ngOnInit(): void {
    this.loadClassmates();
  }

  loadClassmates(): void {
    this.loading = true;
    this.registrationService.getClassmates(this.subjectId, this.currentStudentId).subscribe({
      next: (names) => {
        this.classmateNames = names;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar compañeros de clase';
        this.loading = false;
        console.error(err);
      }
    });
  }
}
