import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Student } from '../../models/student.model';
import { Registration } from '../../models/registration.model';
import { Subject } from '../../models/subject.model';
import { StudentService } from '../../services/student.service';
import { SubjectService } from '../../services/subject.service';
import { RegistrationService } from '../../services/registration.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-student-detail',
  templateUrl: './student-detail.component.html',
  styleUrls: ['./student-detail.component.css']
})
export class StudentDetailComponent implements OnInit {
  student: Student | null = null;
  registrations: Registration[] = [];
  availableSubjects: Subject[] = [];
  selectedSubjectId: number | null = null;
  loading = false;
  error = '';
  registrationError = '';
  isAdmin = false;
  isCurrentUser = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private studentService: StudentService,
    private subjectService: SubjectService,
    private registrationService: RegistrationService,
    private authService: AuthService
  ) { }

  ngOnInit(): void {
    this.isAdmin = this.authService.isAdmin();

    this.route.params.subscribe(params => {
      const id = +params['id'];
      this.loadStudent(id);
      this.loadRegistrations(id);

      // Check if current user is viewing their own profile
      const currentUserId = this.authService.getCurrentUserId();
      this.isCurrentUser = currentUserId === id;
    });
    this.loadSubjects();
  }

  loadStudent(id: number): void {
    this.loading = true;
    this.studentService.getStudent(id).subscribe({
      next: (data) => {
        this.student = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar datos del estudiante';
        this.loading = false;
        console.error(err);
      }
    });
  }

  loadRegistrations(studentId: number): void {
    this.loading = true;
    this.studentService.getStudentRegistrations(studentId).subscribe({
      next: (data) => {
        this.registrations = data;
        this.loading = false;
        this.updateAvailableSubjects();
      },
      error: (err) => {
        this.error = 'Error al cargar registros del estudiante';
        this.loading = false;
        console.error(err);
      }
    });
  }

  loadSubjects(): void {
    this.subjectService.getSubjects().subscribe({
      next: (data) => {
        this.availableSubjects = data;
        this.updateAvailableSubjects();
      },
      error: (err) => {
        console.error('Error al cargar materias', err);
      }
    });
  }

  updateAvailableSubjects(): void {
    if (this.registrations.length > 0 && this.availableSubjects.length > 0) {
      // Filtrar materias ya registradas
      const registeredSubjectIds = this.registrations.map(r => r.subjectId);

      // Filtrar materias con profesores que ya tienen clases con el estudiante
      const registeredProfessorIds = this.registrations
        .map(r => this.availableSubjects.find(s => s.id === r.subjectId)?.professorId)
        .filter(id => id !== undefined) as number[];

      this.availableSubjects = this.availableSubjects.filter(subject => 
        !registeredSubjectIds.includes(subject.id) && 
        !registeredProfessorIds.includes(subject.professorId)
      );
    }
  }

  registerSubject(): void {
    if (!this.student || !this.selectedSubjectId) {
      this.registrationError = 'Debe seleccionar una materia';
      return;
    }

    if (this.registrations.length >= 3) {
      this.registrationError = 'Ya ha registrado el máximo de 3 materias permitidas';
      return;
    }

    this.loading = true;
    this.registrationError = '';

    this.registrationService.registerSubject({
      studentId: this.student.id!,
      subjectId: this.selectedSubjectId
    }).subscribe({
      next: () => {
        this.loading = false;
        this.loadRegistrations(this.student!.id!);
        this.selectedSubjectId = null;
      },
      error: (err) => {
        this.registrationError = err.message || 'Error al registrar materia';
        this.loading = false;
        console.error(err);
      }
    });
  }

  unregisterSubject(registrationId: number): void {
    if (confirm('¿Está seguro de eliminar este registro?')) {
      this.loading = true;
      this.registrationService.unregisterSubject(registrationId).subscribe({
        next: () => {
          this.loading = false;
          this.loadRegistrations(this.student!.id!);
        },
        error: (err) => {
          this.error = 'Error al eliminar registro';
          this.loading = false;
          console.error(err);
        }
      });
    }
  }
}
